
(function($)
{

    $(function ()
    {
        $('#comments-container').comments({
            profilePictureURL: '/node-comments/front-end/user-icon.png',
            roundProfilePictures: true,
            textareaRows: 1,
            getComments: function (success, error)
            {
                $.ajax('/node-comments/all', {
                    type: 'GET',
                    success: function(data){
                        success(data);
                    },
                    error: function(err){
                        console.error(err);
                    }
                });
            },
            postComment: function (data, success, error)
            {
                $.ajax('/node-comments/add', {
                   data: data,
                   type: 'POST',
                    success: function(){
                        success(data);
                    },
                    error: function(err){
                        console.error(err);
                    }
                });
            },
            putComment: function (data, success, error)
            {
                $.ajax('/node-comments/update', {
                    data: data,
                    type: 'POST',
                    success: function(){
                        success(data);
                    },
                    error: function(err){
                        console.error(err);
                    }
                });
            },
            deleteComment: function (data, success, error)
            {
                $.ajax('/node-comments/delete', {
                    data: data,
                    type: 'POST',
                    success: function(){
                        success(data);
                    },
                    error: function(err){
                        console.error(err);
                    }
                });
            },
            upvoteComment: function (data, success, error)
            {
                console.log(data);
                $.ajax('/node-comments/update', {
                    data: data,
                    type: 'POST',
                    success: function(){
                        success(data);
                    },
                    error: function(err){
                        console.error(err);
                    }
                });
            }
        });
    });

}(jQuery));
